import { CommonModule } from '@angular/common';
import {
  AfterViewInit,
  ChangeDetectionStrategy,
  Component,
  HostBinding,
  Renderer2,
  contentChildren,
  forwardRef,
} from '@angular/core';
import { ControlValueAccessor, NG_VALUE_ACCESSOR } from '@angular/forms';
import { nanoid } from 'nanoid';
import { NgxGovukRadioComponent } from './radio.component';

@Component({
  selector: 'ngx-govuk-radio-group',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './radio-group.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => NgxGovukRadioGroupComponent),
      multi: true,
    },
  ],
})
export class NgxGovukRadioGroupComponent
  implements AfterViewInit, ControlValueAccessor
{
  @HostBinding('class') get className() {
    return 'govuk-radios';
  }

  groupName = nanoid();

  radios = contentChildren(NgxGovukRadioComponent);

  value!: string;

  constructor(private renderer: Renderer2) {}

  ngAfterViewInit(): void {
    this.radios().forEach((radio) => {
      this.renderer.setAttribute(
        radio.inputEl()?.nativeElement,
        'name',
        this.groupName
      );
      radio.selected.subscribe((value) => {
        this.onChange(value);
      });
    });
  }

  onChange = (value: string) => {};
  onTouched = () => {};

  writeValue(value: string): void {
    this.value = value;
  }

  registerOnChange(fn: (value: string) => void): void {
    this.onChange = fn;
  }

  registerOnTouched(fn: () => void): void {
    this.onTouched = fn;
  }
}

